	const useAuth = () => {
		const user = localStorage.getItem("user")
		if (user) {
			return true
		} else {
			return false
		}
	}
	const user = useAuth()
	const location = useLocation()
	const navigation = useNavigate()

	const logout = () => {
		localStorage.removeItem("user")
		navigation("/login")
	}

	
}

export default Sidebar
